import React, { useState } from 'react';
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption,
   
} from 'reactstrap';
import { IMAGE_BASE_URL} from '../../config.js';


    const CarouselPage = ({images}) => {
    const [activeIndex, setActiveIndex] = useState(0);
    const [animating, setAnimating] = useState(false);

    const next = () => {
        if (animating) return;
        const nextIndex = activeIndex === images.length - 1 ? 0 : activeIndex + 1;
        setActiveIndex(nextIndex);
    }

    const previous = () => {
        if (animating) return;
        const nextIndex = activeIndex === 0 ? images.length - 1 : activeIndex - 1;
        setActiveIndex(nextIndex);
    }

    const goToIndex = (newIndex) => {
        if (animating) return;
        setActiveIndex(newIndex);
    }

    const slides = images.map((item) => {
        return (
            
        <CarouselItem
            onExiting={() => setAnimating(true)}
            onExited={() => setAnimating(false)}
            key={item.id}
        >
            <div style={{height: '80vh', width: '100%', background:`url(${IMAGE_BASE_URL}${item.backdrop_path})`, backgroundRepeat: 'no-repeat', backgroundSize: 'cover', backgroundPosition:'center'}}>
                <div style={{width:'100%', height: '100%', background: 'linear-gradient(360deg, rgba(0,0,0,1) 0%, rgba(0,0,0,0.8) 18%, rgba(0,0,0,0) 50%, rgba(0,0,0,0) 100%'}}></div>
            </div>
            <CarouselCaption captionText={item.overview} captionHeader={item.title} />
        </CarouselItem>
        
        );
    });

    return (
        <Carousel
        activeIndex={activeIndex}
        next={next}
        previous={previous}
        >
        <CarouselIndicators items={images} activeIndex={activeIndex} onClickHandler={goToIndex} />
        {slides}
        <CarouselControl direction="prev" directionText="Previous" onClickHandler={previous} />
        <CarouselControl direction="next" directionText="Next" onClickHandler={next} />
        </Carousel>
    );
    }
 
export default CarouselPage;
